#!/usr/bin/env python3\n\nimport weaviate\n\n# Connect to Weaviate\nclient = weaviate.connect_to_local()\n\ntry:\n    # Get ChessGame collection schema\n    chess_game = client.collections.get("ChessGame")\n    chess_game_schema = chess_game.config.get()\n    print("=== ChessGame Collection Properties ===")\n    for prop in chess_game_schema.properties:\n        print(f"  - {prop.name}: {prop.data_type}")\n    \n    print()\n    \n    # Get ChessPositions collection schema\n    chess_positions = client.collections.get("ChessPositions")\n    positions_schema = chess_positions.config.get()\n    print("=== ChessPositions Collection Properties ===")\n    for prop in positions_schema.properties:\n        print(f"  - {prop.name}: {prop.data_type}")\n    \n    print()\n    print("=== Sample ChessGame Data ===")\n    # Get a sample game to see actual data structure\n    sample_games = chess_game.query.fetch_objects(limit=1)\n    if sample_games.objects:\n        sample_game = sample_games.objects[0]\n        print("Available properties in sample game:")\n        for key, value in sample_game.properties.items():\n            print(f"  - {key}: {type(value).__name__}")\n    \nfinally:\n    # client.close() removed - Weaviate client manages connections automatically 